import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import checkBoxContainer from './checkBoxContainer';

class App extends Component {

  state = {
    data: [],
    isLoading: false,
    showRepublicans: true,
    showDemocrats: true,
    showOther: true,
  }

  componentDidMount(){
    this.doFetch()
  }

  toggleRepublicans() {
    console.log('elephant');
    this.setState({
      showRepublicans: !this.state.showRepublicans
    })
    // if this.state.showRepublicans === false
    //   then set style="display: none"
  }

  toggleDemocrats() {
    console.log('donkey');
    this.setState({
      showDemocrats: !this.state.showDemocrats
    })
  }

  toggleOthers() {
    console.log('leaf');
    this.setState({
      showOther: !this.state.showOther
    })
  }

  doFetch(){
    console.log('Hitting refresh');
    this.setState({
      isLoading: true,
    });
    fetch("./floridadata.json")
      .then(data => {
        return data.json()}) 
      .then(data => {
        console.log("Got the data!", data);

      this.setState({
        data: data,
      });
    });

  }



  render(){
    return (
      <div className="Container">
        <div className="Container-img">
        </div>
        <div className="Container-title">
          <h1> Florida Voter Registration 1972-2016</h1>
        </div>

        <div className="Container-graph">
          {this.state.data.map(function(entry){


          const number = entry.Number;
          const height = Math.round(entry.Number/5000000*100);

          const heightStyle = height + "%"; 

            return (<p className="bar" style={{height: heightStyle}}>{entry["Number"]}</p>)
          })}
        </div>
        <Checkbox onCheck={this.toggleRepublicans}/>
        <Checkbox onCheck={this.toggleRepublicans}/>
        <Checkbox onCheck={this.toggleRepublicans}/>
      </div>



    );
  }
}

export default App;
