import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Checkbox from './Checkbox'
import {render} from 'react-dom'

const dataPoints =[];


class App extends Component {
  state = {
    party: '',
    year: '',
    number: '',
    bar: '', 
    isLoading:false,
    checked: false
  }

  doFetch = () => {
    console.log('Hitting refresh', this.state.party.year.number);
    this.setState({
      isLoading: true,
    });
    fetch("./data/floridadata.json")
      .then(data => data.json()) //converts file to json 
      .then(data => {
        console.log("Got the data!", data);

      this.setState({
        party: data.party,
        number: data.number,
        height: Math.round(data.number/5000000 * 100),
        year: Math.round(data.year),
        isLoading: false,
      });
    });

  }

  handleCheckboxChange = (ev) =>
    this.setState ( {checked: event.target,checked})

  componentDidMount = (ev) => {
    this.doFetch();
  }



  // onRefresh = () => {
  //   console.log('hitting refresh', this.state);
  //   fetch("./data/floridadata.json")
  //     .then(response => response.json())
  //     .then(data => {
  //       console.log('receiving data', data);
  //     })
  // }
  render() {
    const options = {
      title: {
        text: "Florida Party Affiliation from 1972 to 2006"
      },
      axisY: {
        title: "% out of 5,000,000",
        includeZero: true
      },
      data: [{
        type: "bar",
        xValueFormatString: "YYYY",
        yvalueFormatString: "#,###,###",
        dataPoints: dataPoints
      }]
    }
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>

        <div style={{ fontFamily: 'system-ui' }}>
          <label>
            <Checkbox
              checked={this.state.checked}
              onChange={this.handleCheckboxChange}
            />
            <span style={{ marginLeft:8 }}>Republican</span>
          </label>

          <label>
            <Checkbox
              checked={this.state.checked}
              onChange={this.handleCheckboxChange}
            />
            <span style={{ marginLeft:8 }}>Democrat</span>
          </label>

          <label>
            <Checkbox
              checked={this.state.checked}
              onChange={this.handleCheckboxChange}
            />
            <span style={{ marginLeft:8 }}>Others</span>
          </label>
        </div> 

      </div>
    );
  }
}

export default App;
